mapper.select:
SELECT
ta.*,
tu.id ownerId,
tu.name ownerName
FROM
t_activity ta
JOIN t_user tu
ON ta.owner_id = tu.id


      and ta.owner_id = #{activityQuery.ownerId}


      and ta.name like concat('%',#{activityQuery.activityName},'%')


        and ta.start_time >= #{activityQuery.startTime}


      and ta.end_time <= #{activityQuery.endTime}


      and ta.create_time >= #{activityQuery.createTime}


      and ta.cost = #{activityQuery.cost}


      ${baseQuery.filterSQL}
